import React, {
  Component
} from 'react';
import axios from 'axios';
import {
  Input,
  FormGroup,
  Label,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Table,
  Button
} from 'reactstrap';

class App extends Component {
  state = {
    contacts: [],
    newContactsData: {
      title: '',
      rating: ''
    },
    editContactsData: {
      id: '',
      title: '',
      rating: ''
    },
    newBookModal: false,
    editBookModal: false
  }
  componentWillMount() {
    this._refreshBooks();
  }
  toggleNewBookModal() {
    this.setState({
      newBookModal: !this.state.newBookModal
    });
  }
  toggleEditBookModal() {
    this.setState({
      editBookModal: !this.state.editBookModal
    });
  }
  addBook() {
    axios.post('https://react-phonebook-b7a92.firebaseio.com', this.state.newContactsData).then((response) => {
      let {
        contacts
      } = this.state;

      contacts.push(response.data);

      this.setState({
        contacts,
        newBookModal: false,
        newContactsData: {
          title: '',
          rating: ''
        }
      });
    });
  }
  updateBook() {
    let {
      title,
      rating
    } = this.state.editContactsData;

    axios.put('https://react-phonebook-b7a92.firebaseio.com' + this.state.editContactsData.id, {
      title,
      rating
    }).then((response) => {
      this._refreshBooks();

      this.setState({
        editBookModal: false,
        editContactsData: {
          id: '',
          title: '',
          rating: ''
        }
      })
    });
  }
  editBook(id, title, rating) {
    this.setState({
      editContactsData: {
        id,
        title,
        rating
      },
      editBookModal: !this.state.editBookModal
    });
  }
  deleteBook(id) {
    axios.delete('https://react-phonebook-b7a92.firebaseio.com' + id).then((response) => {
      this._refreshBooks();
    });
  }
  _refreshBooks() {
    axios.get('https://react-phonebook-b7a92.firebaseio.com').then((response) => {
      this.setState({
        contacts: response.data
      })
    });
  }
  render() {
    let contacts = this.state.contacts.map((book) => {
      return ( <
        tr key = {
          book.id
        } >
        <
        td > {
          book.id
        } < /td> <
        td > {
          book.title
        } < /td> <
        td > {
          book.rating
        } < /td> <
        td >
        <
        Button color = "success"
        size = "sm"
        className = "mr-2"
        onClick = {
          this.editBook.bind(this, book.id, book.title, book.rating)
        } > Edit < /Button> <
        Button color = "danger"
        size = "sm"
        onClick = {
          this.deleteBook.bind(this, book.id)
        } > Delete < /Button> < /
        td > <
        /tr>
      )
    });
    return ( <
      div className = "App container" >

      <
      h1 > Books App < /h1>

      <
      Button className = "my-3"
      color = "primary"
      onClick = {
        this.toggleNewBookModal.bind(this)
      } > Add Book < /Button>

      <
      Modal isOpen = {
        this.state.newBookModal
      }
      toggle = {
        this.toggleNewBookModal.bind(this)
      } >
      <
      ModalHeader toggle = {
        this.toggleNewBookModal.bind(this)
      } > Add a new book < /ModalHeader> <
      ModalBody >
      <
      FormGroup >
      <
      Label
      for = "title" > Title < /Label> <
      Input id = "title"
      value = {
        this.state.newContactsData.title
      }
      onChange = {
        (e) => {
          let {
            newContactsData
          } = this.state;

          newContactsData.title = e.target.value;

          this.setState({
            newContactsData
          });
        }
      }
      /> < /
      FormGroup > <
      FormGroup >
      <
      Label
      for = "rating" > Rating < /Label> <
      Input id = "rating"
      value = {
        this.state.newContactsData.rating
      }
      onChange = {
        (e) => {
          let {
            newContactsData
          } = this.state;

          newContactsData.rating = e.target.value;

          this.setState({
            newContactsData
          });
        }
      }
      /> < /
      FormGroup >

      <
      /ModalBody> <
      ModalFooter >
      <
      Button color = "primary"
      onClick = {
        this.addBook.bind(this)
      } > Add Book < /Button>{' '} <
      Button color = "secondary"
      onClick = {
        this.toggleNewBookModal.bind(this)
      } > Cancel < /Button> < /
      ModalFooter > <
      /Modal>

      <
      Modal isOpen = {
        this.state.editBookModal
      }
      toggle = {
        this.toggleEditBookModal.bind(this)
      } >
      <
      ModalHeader toggle = {
        this.toggleEditBookModal.bind(this)
      } > Edit a new book < /ModalHeader> <
      ModalBody >
      <
      FormGroup >
      <
      Label
      for = "title" > Title < /Label> <
      Input id = "title"
      value = {
        this.state.editContactsData.title
      }
      onChange = {
        (e) => {
          let {
            editContactsData
          } = this.state;

          editContactsData.title = e.target.value;

          this.setState({
            editContactsData
          });
        }
      }
      /> < /
      FormGroup > <
      FormGroup >
      <
      Label
      for = "rating" > Rating < /Label> <
      Input id = "rating"
      value = {
        this.state.editContactsData.rating
      }
      onChange = {
        (e) => {
          let {
            editContactsData
          } = this.state;

          editContactsData.rating = e.target.value;

          this.setState({
            editContactsData
          });
        }
      }
      /> < /
      FormGroup >

      <
      /ModalBody> <
      ModalFooter >
      <
      Button color = "primary"
      onClick = {
        this.updateBook.bind(this)
      } > Update Book < /Button>{' '} <
      Button color = "secondary"
      onClick = {
        this.toggleEditBookModal.bind(this)
      } > Cancel < /Button> < /
      ModalFooter > <
      /Modal>


      <
      Table >
      <
      thead >
      <
      tr >
      <
      th > # < /th> <
      th > Title < /th> <
      th > Rating < /th> <
      th > Actions < /th> < /
      tr > <
      /thead>

      <
      tbody > {
        contacts
      } <
      /tbody> < /
      Table > <
      /div>
    );
  }
}

export default App;
